% Construct the matrix correponding to the recursive Koszul flattening
N=nchoosek(6,1)*nchoosek(6,2)*nchoosek(6,3)*nchoosek(6,4)*n;
kosz=sparse(N,N);
flat=zeros(n,n,n,n,n,n);
for p = 1:n
    diff1 = diff(det,a(1,p));
    for q = 1:n
        if length(unique([p,q])) ~= length([p,q])
            continue
        end
        diff2 = diff(diff1,a(2,q));
        kron12 = sparse(kron(psi1(:,:,p),psi2(:,:,q)));
        for r = 1:n
            if length(unique([p,q,r])) ~= length([p,q,r])
                continue
            end
            diff3 = diff(diff2,a(3,r));
            sparsePsi = sparse(psi3(:,:,r));
            kron123 = spkron(kron12,sparsePsi);
            for s = 1:n
                if length(unique([p,q,r,s])) ~= length([p,q,r,s])
                    continue
                end
                diff4 = diff(diff3,a(4,s));
                sparsePsi = sparse(psi4(:,:,s));
                kron1234 = spkron(kron123,sparsePsi);
                for i = 1:n
                    if length(unique([p,q,r,s,i])) ~= length([p,q,r,s,i])
                        continue
                    end
                    diff5 = diff(diff4,a(5,i));
                    for j = 1:n
                        if length(unique([p,q,r,s,i,j])) ~= length([p,q,r,s,i,j])
                            continue
                        end
                        flat(i,j,p,q,r,s) = abs(diff(diff5,a(6,j)));
                        disp([p,q,r,s,i,j]);
                    end
                end
                kosz = kosz + spkron(kron1234,sparse(flat(:,:,p,q,r,s)));
            end
        end
    end
end

% Calculate the rank
orbitmat = readmatrix('orbitmat6.dat');
tic;
rk = getRankSymm(kosz, orbitmat, 6)    % rank: 70692, lower bound of brk(Per6): 29
x=toc;
